import React, { useState, useRef, useEffect } from 'react';
import { chatbotAPI } from '../services/api';

const bubbleStyle = {
  maxWidth: '80%',
  padding: '10px 12px',
  borderRadius: '12px',
  margin: '6px 0',
  lineHeight: 1.35,
  fontSize: 14
};

export default function ChatWidget() {
  const [open, setOpen] = useState(false);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([
    { role: 'assistant', content: "Hello! I'm your AyurSutra assistant. How can I help you today?" }
  ]);
  const listRef = useRef(null);

  useEffect(() => {
    if (listRef.current) {
      listRef.current.scrollTop = listRef.current.scrollHeight;
    }
  }, [messages, open]);

  const send = async () => {
    const text = input.trim();
    if (!text || loading) return;
    const nextMsgs = [...messages, { role: 'user', content: text }];
    setMessages(nextMsgs);
    setInput('');
    setLoading(true);
    try {
      const { data } = await chatbotAPI.sendMessage({
        message: text,
        history: nextMsgs.slice(-6) // send small context window
      });
      const reply = data?.reply || "I'm here to help.";
      setMessages(prev => [...prev, { role: 'assistant', content: reply }]);
    } catch (e) {
      setMessages(prev => [...prev, { role: 'assistant', content: 'Sorry, I could not process that right now.' }]);
    } finally {
      setLoading(false);
    }
  };

  const onKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  // Floating toggle button when closed
  if (!open) {
    return (
      <button
        aria-label="Open chat"
        onClick={() => setOpen(true)}
        style={{
          position: 'fixed',
          right: 20,
          bottom: 20,
          borderRadius: '9999px',
          width: 56,
          height: 56,
          background: '#0ea5e9',
          color: 'white',
          border: 'none',
          boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
          cursor: 'pointer',
          fontWeight: 700
        }}
      >
        Chat
      </button>
    );
  }

  // Opened chat window
  return (
    <div
      style={{
        position: 'fixed',
        right: 20,
        bottom: 20,
        width: 340,
        height: 460,
        background: 'white',
        borderRadius: 12,
        boxShadow: '0 12px 32px rgba(0,0,0,0.25)',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'hidden',
        border: '1px solid #e5e7eb',
        zIndex: 50
      }}
    >
      <div
        style={{
          background: '#0ea5e9',
          color: 'white',
          padding: '10px 12px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}
      >
        <div style={{ fontWeight: 700 }}>AyurSutra Assistant</div>
        <button
          onClick={() => setOpen(false)}
          style={{
            background: 'transparent',
            border: 'none',
            color: 'white',
            fontSize: 16,
            cursor: 'pointer'
          }}
          aria-label="Close chat"
        >
          ✕
        </button>
      </div>

      <div
        ref={listRef}
        style={{
          flex: 1,
          padding: '12px',
          overflowY: 'auto',
          background: '#f8fafc'
        }}
      >
        {messages.map((m, idx) => (
          <div key={idx} style={{ display: 'flex', justifyContent: m.role === 'user' ? 'flex-end' : 'flex-start' }}>
            <div
              style={{
                ...bubbleStyle,
                background: m.role === 'user' ? '#0ea5e9' : '#e5e7eb',
                color: m.role === 'user' ? 'white' : '#111827'
              }}
            >
              {m.content}
            </div>
          </div>
        ))}
        {loading && (
          <div style={{ opacity: 0.7, fontSize: 12, marginTop: 6 }}>Assistant is typing…</div>
        )}
      </div>

      <div style={{ padding: 10, borderTop: '1px solid #e5e7eb', background: 'white' }}>
        <div style={{ display: 'flex', gap: 8 }}>
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={onKeyDown}
            placeholder="Type your message..."
            rows={2}
            style={{
              flex: 1,
              resize: 'none',
              padding: 8,
              borderRadius: 8,
              border: '1px solid #d1d5db',
              fontSize: 14
            }}
          />
          <button
            onClick={send}
            disabled={loading}
            style={{
              background: loading ? '#93c5fd' : '#0ea5e9',
              color: 'white',
              border: 'none',
              borderRadius: 8,
              padding: '8px 12px',
              cursor: loading ? 'not-allowed' : 'pointer',
              fontWeight: 600
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
